import React from 'react';
import './App.css';
import Column from './Components/Column.js';
import MenuSection from './Components/MenuSection';
// ... other imports

const REFRESH_INTERVAL = 30000; // Refresh interval in milliseconds (30 seconds)

const fetchData = async (url) => {
  const response = await axios.get(url);
  return response.data;
};

const queryClient = new QueryClient();

function App() {
  const { isLoading, error, data } = useQuery({
    queryKey: ['menuData'],
    queryFn: () => fetchData('http://localhost:8000/menu').then((res) => res.json()),
  });

  if (isLoading) return <div>Loading menu...</div>;
  if (error) return <div>Error fetching data: {error.message}</div>;

  return (
    <div className="w-5/6 mx-auto flex h-screen">
      {/* ... */}
      <Column width="w-1/2">
        <MenuSection data={data} descriptions={data[0].note} />
        {/* ... */}
      </Column>
      {/* ... */}
    </div>
  );
}

export default function WrappedApp() {
  return (
    <QueryClientProvider client={queryClient}>
      <App />
    </QueryClientProvider>
  );
}
