import React from 'react';
import MenuItem from './MenuItem';

const MenuSection = ({ data }) => {
  // Assuming data is an array of objects containing menu items
  return (
    <div className="mt-8">
      {data.map((menuCategory, index) => (
        <div key={index}>
          <h2 className="text-xl font-semibold">{Object.keys(menuCategory)[0]}</h2>
          <hr className="mt-2 w-full border-t border-gray-500" />
          {Object.values(menuCategory)[0].map((item, idx) => (
            <MenuItem
              key={idx}
              name={item.name}
              description={item.description}
              price={item.price}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

export default MenuSection;
