import React from 'react';

const SelectionMenuWithNote = ({ title, note, choices }) => {
  return (
    <div className="mt-8 w-full">
      <div className="p-4">
        <div className="border border-gray-500 mb-4 rounded inline-block p-2">
          <h2 className="text-xl font-semibold">{title}</h2>
          <p className="font-bold">{note}</p>
        </div>
        <div className="flex flex-wrap justify-center">
          {choices.map((choice, index) => (
            <React.Fragment key={index}>
              <div className="pr-2">{choice}</div>
              {index !== choices.length - 1 && <div className="pr-2">&middot;</div>}
            </React.Fragment>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SelectionMenuWithNote;
