import React from 'react';

const MenuHeaderWithDescription = ({ title, description }) => {
  return (
    <div className="mt-8 flex flex-col items-center">
      <h2 className="text-xl font-semibold">{title}</h2>
      {description && <p className="text-sm" style={{ maxWidth: '400px' }}>{description}</p>} {/* Adjust max-width as needed */}
      <hr className="mt-2 w-full border-t border-gray-500" style={{ width: '80%' }} />
    </div>
  );
};

export default MenuHeaderWithDescription;
