import React from 'react';

const MenuHeaderWithDescription = ({ section, description }) => {
  return (
    <div className="mt-8 flex flex-col items-center"> {/* Center the content horizontally */}
      <h2 className="text-xl font-semibold">{section}</h2>
      {description && <p className="text-sm text-center mt-2">{description}</p>} {/* Render description if available */}
      <hr className="mt-2 w-full border-t border-gray-500" style={{ width: '80%' }} /> {/* Adjust the width here */}
    </div>
  );
};

export default MenuHeaderWithDescription;
