import React from 'react';

const SelectionMenu = ({ title, choices }) => {
  // Calculate the border width based on the length of the title
  const borderWidth = title.length * 3; // Adjust the multiplier as needed

  return (
    <div className="mt-8 w-full">
      <div className="p-4">
        <div className="rounded border border-gray-500 mb-4 p-2" style={{ borderWidth: `${borderWidth}px` }}>
          <h2 className="text-xl font-semibold">{title}</h2>
        </div>
        <div className="flex flex-wrap justify-center">
          {choices.map((choice, index) => (
            <React.Fragment key={index}>
              <div className="pr-2">{choice}</div>
              {index !== choices.length - 1 && <div className="pr-2">&middot;</div>}
            </React.Fragment>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SelectionMenu;
