import React from 'react';
import MenuHeaderWithDescription from './MenuHeaderWithDescription';

const MenuSection = ({ data, descriptions }) => { // Pass descriptions as prop
  return (
    <div className="mt-8">
      <MenuHeaderWithDescription
        section={data.section}
        description={descriptions[data.section]} // Pass description based on section title
      />
      {/* Render menu items */}
    </div>
  );
};

export default MenuSection;

