import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import Column from './Components/Column.js';
import MenuSectionWithNote from './Components/MenuSectionWithNote.js'; // Import the new component
import TriColumnSection from './Components/TriColumnSection.js';
import axios from 'axios';
import { useQuery, QueryClient, QueryClientProvider } from 'react-query';

const REFRESH_INTERVAL = 30000; // Refresh interval in milliseconds (30 seconds)

const fetchData = async (url) => {
  const response = await axios.get(url);
  return response.data;
};

const queryClient = new QueryClient();

function App() {
  const leftColumnRef = useRef(null);
  const rightColumnRef = useRef(null);

  const { data: fromTheGulfAndPondData } = useQuery('fromTheGulfAndPond', () => fetchData('/data/from_the_gulf_and_pond.json')); // Fetch the new data

  // Fetch the section headers data
  const { data: sectionHeaders } = useQuery('sectionHeaders', () => fetchData('/data/section_headers.json'));

  useEffect(() => {
    const fetchDataPeriodically = () => {
      fetchData('/data/from_the_gulf_and_pond.json'); // Fetch the new data periodically
    };

    const intervalId = setInterval(fetchDataPeriodically, REFRESH_INTERVAL);

    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    function setEqualHeight() {
      if (leftColumnRef.current && rightColumnRef.current) {
        const leftHeight = leftColumnRef.current.offsetHeight;
        const rightHeight = rightColumnRef.current.offsetHeight;
        const maxHeight = Math.max(leftHeight, rightHeight);
        leftColumnRef.current.style.height = `${maxHeight}px`;
        rightColumnRef.current.style.height = `${maxHeight}px`;
      }
    }

    // Call setEqualHeight when the window is resized
    window.addEventListener('resize', setEqualHeight);

    // Call setEqualHeight once initially
    setEqualHeight();

    // Cleanup event listener
    return () => window.removeEventListener('resize', setEqualHeight);
  }, []);

  return (
    <div className="w-5/6 mx-auto flex flex-col">
      <div className="flex flex-col md:flex-row">
        <Column width="w-full md:w-1/2" ref={leftColumnRef}>
          <MenuSectionWithNote data={{ title: "From the Gulf and Pond", items: fromTheGulfAndPondData }} descriptions={sectionHeaders} /> {/* Render the new component with the fetched data */}
        </Column>
        <Column width="w-full md:w-1/2" ref={rightColumnRef}>
          {/* Render other components as needed */}
        </Column>
      </div>
    </div>
  );
}

export default function WrappedApp() {
  return (
    <QueryClientProvider client={queryClient}>
      <App />
    </QueryClientProvider>
  );
}
