import React from 'react';

const MenuItem = ({ name, description, price }) => {
  return (
    <div className="my-4">
      <div className="flex justify-between">
        <div>
          <h3 className="text-xs font-semibold">{name}</h3> {/* Smaller font size */}
          <p className="text-xs">{description}</p> {/* Smaller font size */}
        </div>
        <div>
          <p className="text-xs font-bold text-right">${price}</p> {/* Smaller font size, right-aligned */}
        </div>
      </div>
    </div>
  );
};

export default MenuItem;
