// MenuItem.js
import React from 'react';

const MenuItem = ({ name, description, price }) => {
  return (
    <div className="my-4">
      <div className="flex justify-between items-start"> {/* Add 'items-start' class */}
        <div>
          <h3 className="text-sm font-semibold">{name}</h3> {/* Change font size to 'text-sm' */}
          <p className="text-sm">{description}</p> {/* Change font size to 'text-sm' */}
        </div>
        <div className="pl-2"> {/* Add padding between price and next item name */}
          <p className="text-sm font-bold">${price}</p> {/* Change font size to 'text-sm' */}
        </div>
      </div>
    </div>
  );
};

export default MenuItem;
