import React from 'react';

const TriColumnSection = ({ title, items }) => {
  return (
    <div className="border border-gray-300 p-4 w-full">
      <h2 className="text-lg font-semibold mb-4">{title}</h2>
      <div className="flex justify-between">
        <div className="w-1/3">
          {items.slice(0, Math.ceil(items.length / 3)).map((item, index) => (
            <div key={index} className="bg-gray-100 p-2 mb-4">{item}</div>
          ))}
        </div>
        <div className="w-1/3">
          {items.slice(Math.ceil(items.length / 3), Math.ceil(2 * items.length / 3)).map((item, index) => (
            <div key={index} className="bg-gray-100 p-2 mb-4">{item}</div>
          ))}
        </div>
        <div className="w-1/3">
          {items.slice(Math.ceil(2 * items.length / 3)).map((item, index) => (
            <div key={index} className="bg-gray-100 p-2 mb-4">{item}</div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TriColumnSection;
