import React from 'react';

const Notes = ({ notes }) => {
  return (
    <div className="border border-gray-300 rounded p-4 border-2"> {/* Added border-2 class for thicker border */}
      {notes.map((note, index) => (
        <div key={index} className="mb-4">
          <p className="font-bold">{note.section}: <span className="font-normal">{note.description}</span></p>
        </div>
      ))}
    </div>
  );
};

export default Notes;
