import React from 'react';
import MenuItem from './MenuItem';
import MenuHeaderWithDescription from './MenuHeaderWithDescription'; // Import MenuHeaderWithDescription component

const MenuSection = ({ data, descriptions }) => {
  // Check if the items array is empty
  if (data.items.length === 0) {
    return (
      <div className="mt-8">
        <MenuHeaderWithDescription title={data.title} description={descriptions[data.title]} /> {/* Render MenuHeaderWithDescription with the section title and description */}
        <div>No menu items available</div>
      </div>
    );
  }

  return (
    <div className="mt-8">
      <MenuHeaderWithDescription title={data.title} description={descriptions[data.title]} /> {/* Render MenuHeaderWithDescription with the section title and description */}
      {data.items.map((menuItem, index) => (
        <div key={index}>
          <MenuItem
            name={menuItem.name}
            description={menuItem.description}
            price={menuItem.price}
          />
        </div>
      ))}
    </div>
  );
};

export default MenuSection;


