import React from 'react';
import MenuItem from './MenuItem';

const MenuSection = ({ data }) => {
  // Check if data is null or undefined, or if it's not an array
  if (!data || !Array.isArray(data) || data.length === 0) {
    return <div>No menu items available</div>;
  }

  // Extract the menu items object from the array
  const menuItems = data[0];

  return (
    <div className="mt-8">
      {/* Iterate over the keys of the menuItems object */}
      {Object.keys(menuItems).map((key) => {
        const menuItem = menuItems[key];
        return (
          <div key={key}>
            <h2 className="text-xl font-semibold">{menuItem.name}</h2>
            <p>{menuItem.description}</p>
            <p className="font-bold">${menuItem.price}</p>
          </div>
        );
      })}
    </div>
  );
};

export default MenuSection;
