import React from 'react';
import MenuItem from './MenuItem';

const MenuSection = ({ data }) => {
  if (!data || data.length === 0) {
    return <div>No menu items available</div>;
  }

  const menuItems = data[0]; // Extract the object from the array

  return (
    <div className="mt-8">
      {Object.entries(menuItems).map(([key, menuItem]) => (
        <div key={key}>
          <h2 className="text-xl font-semibold">{menuItem.name}</h2>
          <p>{menuItem.description}</p>
          <p className="font-bold">${menuItem.price}</p>
        </div>
      ))}
    </div>
  );
};

export default MenuSection;
